refentry.refsect1:
&reftitle.parameters;


  literal



    The text content of the (mutable) text node.

  &reftitle.examples;



    Basic usage


<?php
use CommonMark\Node\Paragraph;
use CommonMark\Node\Document;
use CommonMark\Node\Text;

$text1 = new Text("Hello");
$text2 = new Text(" Markdown");
$text3 = new Text(" World!");

$paragraph = new Paragraph();
$paragraph->appendChild($text1);
$paragraph->appendChild($text2);
$paragraph->appendChild($text3);

$document = new Document();
$document->appendChild($paragraph);

// <p>Hello Markdown World!</p>

$text2->literal = " PHP";

// <p>Hello PHP World!</p>
