refentry.refsect1:
&reftitle.description;

 public ######### TODOCommonMark\Node::unlink




 Removes the node from the document tree it is attached to.

  &reftitle.examples;



    Basic usage


<?php
use CommonMark\Node\Paragraph;
use CommonMark\Node\Document;
use CommonMark\Node\Text;

$text1 = new Text("Hello");
$text2 = new Text(" Markdown");
$text3 = new Text(" World!");

$paragraph = new Paragraph();
$paragraph->prependChild($text3);
$paragraph->prependChild($text2);
$paragraph->prependChild($text1);

$document = new Document();
$document->prependChild($paragraph);

// <p>Hello Markdown World!</p>

$text2->unlink();

// <p>Hello World!</p>
